let target = flow.get('target') || 0;
let timer = context.get('timer') || null;
let mode = msg.payload; // 0 for stop, 1 for decrement, 2 for increment
let interval = 120; // 120ms interval

// Function to clear the timer and send the current count
function sendAndClear() {
    clearTimeout(timer);
    context.set('timer', null);
    flow.set("sence", true);
    msg.payload = { "CurrentPosition": target, "TargetPosition": target };
    node.send(msg);
}

if (mode === 0) {
    // If mode is 0, stop the counting and send the current target immediately
    sendAndClear();
} else if (mode === 1) {
    if (timer) return null; // If already counting, ignore the input
    function increment() {
        target++;
        if (target >= 100) {
            target = 100;
            sendAndClear();
        } else {
            flow.set('count', target);
            timer = setTimeout(increment, interval);
            context.set('timer', timer);
        }
    }
    increment();
} else if (mode === 2) {
    if (timer) return null; // If already counting, ignore the input
    function decrement() {
        target--;
        if (target <= 0) {
            target = 0;
            sendAndClear();
        } else {
            flow.set('target', target);
            timer = setTimeout(decrement, interval);
            context.set('timer', timer);
        }
    }
    decrement();
} else {
    return null; // Invalid input, do nothing
}

